#s='BANANA'
s=''
def minion_game(string):
    vowels = ['a', 'e', 'i', 'o', 'u']
    s = string.lower()
    length = len(s)
    vowelP =0
    constP =0



    for i in range(length):
        if s[i] in vowels:
            vowelP+=length-i
        else:
            constP+=length-i


    if vowelP > constP:
        print('Kevin', vowelP)
    elif vowelP == constP:
        print('Draw')
    else:
        print('Stuart', constP)

minion_game(s)